import React, { useState } from "react";
import "./DesignPortfolio.css"; 

const DesignPortfolio = ({portfolioItems}) => {
  const [fullscreenImage, setFullscreenImage] = useState(null);

  const openFullscreenImage = (imageUrl) => {
    setFullscreenImage(imageUrl);
  };

  const closeFullscreenImage = () => {
    setFullscreenImage(null);
  };
  portfolioItems = [
    {
      id: 1,
      imageUrl: "/pubImages/heroImg.webp",
      description: "Description of Image 1",
    },
    {
      id: 2,
      imageUrl: "/pubImages/heroImg.webp",
      description: "Description of Image 2",
    },
    
 
  ];

  return (
    // <section className="design-portfolio">
    //   <h2>Design Portfolio</h2>
    //   <div className="portfolio-grid">
    //     {portfolioItems.map((item) => (
    //       <div key={item.id} className="portfolio-item">
    //         <img src={item.imageUrl} alt={`Design ${item.id}`} />
    //         <p>{item.description}</p>
    //       </div>
    //     ))}
    //   </div>
    // </section>
    <section className="design-portfolio">
      <h2>Design Portfolio</h2>
      <div className="portfolio-grid">
        {portfolioItems.map((item) => (
          <div
            key={item.id}
            className="portfolio-item"
            onClick={() => openFullscreenImage(item.imageUrl)}
          >
            <img src={item.imageUrl} alt={`Design ${item.id}`} />
            <p>{item.description}</p>
          </div>
        ))}
      </div>
      {fullscreenImage && (
        <div className="fullscreen-image">
          <img
            src={fullscreenImage}
            alt="Fullscreen"
            onClick={closeFullscreenImage}
          />
        </div>
      )}
    </section>
  );
  // );
};

export default DesignPortfolio;
