// export default DesignPortfolio;
import React, { useState } from "react";
import "./DesignPortfolio.css"; 
import ImageModal from "../ImageModal/ImageModal";

const DesignPortfolio = ({ portfolioItems = [] }) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedImage, setSelectedImage] = useState("");
  const [selectedTag, setSelectedTag] = useState("");

  const openModal = (imageUrl) => {
    setSelectedImage(imageUrl);
    setModalVisible(true);
  };

  const closeModal = () => {
    setSelectedImage("");
    setModalVisible(false);
  };

  // Filter the portfolio items based on the selected tag
  const filteredItems = selectedTag
    ? portfolioItems.filter((item) => item.tags.includes(selectedTag))
    : portfolioItems;

  // Function to handle tag selection
  const handleTagSelect = (tag) => {
    setSelectedTag(tag);
  };

  return (
    <section className="design-portfolio">
      <h2>Design Portfolio</h2>
      <div className="portfolio-flex">
        {filteredItems.map((item) => (
          <div
            key={item.id}
            className="portfolio-item"
            onClick={() => openModal(item.imageUrl)}
          >
            <img src={item.imageUrl} alt={`Design ${item.id}`} />
            <p>{item.description}</p>
          </div>
        ))}
      </div>
      {modalVisible && (
        <ImageModal imageUrl={selectedImage} onClose={closeModal} />
      )}
    </section>
  );
};

export default DesignPortfolio;
